import {
  SET_INKS,
  LOADING_DATA,
  LIKE_INK,
  UNLIKE_INK,
  DELETE_INK,
  SET_ERRORS,
  POST_INK,
  CLEAR_ERRORS,
  LOADING_UI,
  SET_INK,
  STOP_LOADING_UI,
  SUBMIT_COMMENT
} from '../types';

import axios from 'axios';

export const getInks = () => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get('/inks')
    .then((res) => {
      dispatch({
        type: SET_INKS,
        payload: res.data
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_INKS,
        payload: []
      });
    });
};
