/* eslint-disable */
import {
  SET_INKS,
  LOADING_DATA,
  LIKE_INK,
  UNLIKE_INK,
  DELETE_INK,
  SET_ERRORS,
  POST_INK,
  CLEAR_ERRORS,
  LOADING_UI,
  SET_INK,
  STOP_LOADING_UI,
  SUBMIT_COMMENT
} from '../types';

import axios from 'axios';

export const getInks = () => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get('/inks')
    .then((res) => {
      dispatch({
        type: SET_INKS,
        payload: res.data
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_INKS,
        payload: []
      });
    });
};

export const getInk = (Id) => (dispatch) => {
  dispatch({ type: LOADING_UI });
  axios
    .get(`//${Id}`)
    .then((res) => {
      dispatch({
        type: SET_SCREAM,
        payload: res.data
      });
      dispatch({ type: STOP_LOADING_UI });
    })
    .catch((err) => console.log(err));
};

// Post a 
export const postInk = (newInk) => (dispatch) => {
  dispatch({ type: LOADING_UI });
  axios
    .post('/', newInk)
    .then((res) => {
      dispatch({
        type: POST_SCREAM,
        payload: res.data
      });
      dispatch(clearErrors());
    })
    .catch((err) => {
      dispatch({
        type: SET_ERRORS,
        payload: err.response.data
      });
    });
};
// Like a 
export const likeInk = (Id) => (dispatch) => {
  axios
    .get(`//${Id}/like`)
    .then((res) => {
      dispatch({
        type: LIKE_SCREAM,
        payload: res.data
      });
    })
    .catch((err) => console.log(err));
};
// Unlike a 
export const unlikeInk = (screamId) => (dispatch) => {
  axios
    .get(`//${Id}/unlike`)
    .then((res) => {
      dispatch({
        type: UNLIKE_SCREAM,
        payload: res.data
      });
    })
    .catch((err) => console.log(err));
};
// Submit a comment
export const submitComment = (Id, commentData) => (dispatch) => {
  axios
    .post(`//${Id}/comment`, commentData)
    .then((res) => {
      dispatch({
        type: SUBMIT_COMMENT,
        payload: res.data
      });
      dispatch(clearErrors());
    })
    .catch((err) => {
      dispatch({
        type: SET_ERRORS,
        payload: err.response.data
      });
    });
};
export const deleteScream = (Id) => (dispatch) => {
  axios
    .delete(`//${Id}`)
    .then(() => {
      dispatch({ type: DELETE_SCREAM, payload: Id });
    })
    .catch((err) => console.log(err));
};

export const getUserData = (userHandle) => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get(`/user/${userHandle}`)
    .then((res) => {
      dispatch({
        type: SET_SCREAMS,
        payload: res.data.s
      });
    })
    .catch(() => {
      dispatch({
        type: SET_SCREAMS,
        payload: null
      });
    });
};

export const clearErrors = () => (dispatch) => {
  dispatch({ type: CLEAR_ERRORS });
};
